    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        // Konfirmasi untuk keluar dari aplikasi
    int response = JOptionPane.showConfirmDialog(this, "Apakah Anda yakin ingin keluar?", "Konfirmasi", JOptionPane.YES_NO_OPTION);
    
    // Jika pengguna memilih "Yes", maka aplikasi akan ditutup
    if (response == JOptionPane.YES_OPTION) {
        System.exit(0);
    }
    }                                        

    private void clearFields() {
    NIM.setText("");
    Nama.setText("");
    Alamat.setText("");
    Pria.setSelected(false);
    Wanita.setSelected(false);
    Prodi.setSelectedIndex(0);
    Jurusan.setSelectedIndex(0);
}
    
