    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        // Jika tidak ada data yang tersimpan, tampilkan pesan peringatan
    if (Mahasantri.isEmpty()) {
        JOptionPane.showMessageDialog(this, "Tidak ada data yang tersimpan!", "Informasi", JOptionPane.WARNING_MESSAGE);
    } else {
        // Jika ada data, tampilkan semua data dalam dialog
        StringBuilder allData = new StringBuilder();
        for (String data : Mahasantri) {
            allData.append(data).append("\n-----------------------\n");
        }

        JTextArea textArea = new JTextArea(allData.toString());
        textArea.setEditable(false);
        JScrollPane scrollPane = new JScrollPane(textArea);
        scrollPane.setPreferredSize(new java.awt.Dimension(400, 200));

        JOptionPane.showMessageDialog(this, scrollPane, "Data Mahasiswa", JOptionPane.INFORMATION_MESSAGE);
    }
    }                                        

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {                                         
        // TODO add your handling code here:
        // Memeriksa apakah ada field yang kosong atau gender belum dipilih
    if (NIM.getText().isEmpty() || Nama.getText().isEmpty() || Alamat.getText().isEmpty() ||
        (!Pria.isSelected() && !Wanita.isSelected())) {
        JOptionPane.showMessageDialog(this, "Harap isi semua field!", "Peringatan", JOptionPane.WARNING_MESSAGE);
        return;
    }

    // Mendapatkan nilai gender berdasarkan pilihan
    String gender = Pria.isSelected() ? "Pria" : "Wanita";

    // Membuat string data yang berisi informasi mahasiswa
    String data = "NIM: " + NIM.getText() + "\n" +
                  "Nama: " + Nama.getText() + "\n" +
                  "Alamat: " + Alamat.getText() + "\n" +
                  "Jenis Kelamin: " + gender + "\n" +
                  "Program Studi: " + Prodi.getSelectedItem() + "\n" +
                  "Jurusan: " + Jurusan.getSelectedItem() + "\n";

    // Menambahkan data ke dalam koleksi atau daftar Biodata
    Mahasantri.add(data);

    // Menampilkan pesan bahwa data telah berhasil disimpan
    JOptionPane.showMessageDialog(this, "Data berhasil disimpan!", "Informasi", JOptionPane.INFORMATION_MESSAGE);

    // Membersihkan semua field input
    clearFields();
    }                                        

